"use strict";

var mongoose = require("mongoose");
var Schema = mongoose.Schema;
var OrderSchema = new Schema({
  userId: {
    type: String,
    required: true
  },
  customerId: {
    type: String,
    required: true
  },
  productId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Product"
  },
  quantity: {
    type: Number,
    required: true
  },
  subtotal: {
    type: Number,
    required: true
  },
  total: {
    type: Number,
    required: true
  },
  delivery_status: {
    type: Number,
    required: true
  },
  payment_status: {
    type: String,
    required: true
  }
}, {
  timestamps: true
});
module.exports = mongoose.model("Order", OrderSchema);
