var mongoose = require("mongoose");
var Schema = mongoose.Schema;
var CartSchema = new Schema({
  userId: {
    type: String,
    required: true
  },
  products: [{
    cartItem: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "Product"
    },
    quantity: {
      type: Number,
      "default": 1
    }
  }]
}, {
  timestamps: true
});
module.exports = mongoose.model("Cart", CartSchema);
